<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Registration extends MX_Controller {			public function __construct(){		parent::__construct();		if(!$this->session->userdata('email') and !$this->session->userdata('password') and !$this->session->userdata('name') and !$this->session->userdata('level')){			redirect('auth');		}	}		public function index(){		$data['subhead']= ' ';		$data['head']   = ' ';		$data['icon']	= ' ';		$this->page->view('school_blank',$data);	}	}/* End of file dashboard.php *//* Location: ./application/controllers/dashboard.php */